SELECT COUNT(Survived) FROM train_survival WHERE Survived = 1; -- Total number of passengers that survived is 342
SELECT count(Survived) FROM train_survival WHERE Survived = 0; -- Total number of passengers that did not survive is 549
SELECT name, sex FROM passenger_details JOIN travel_details ON passenger_details.PassengerId = travel_details.PassengerId WHERE Age < 27 AND travel_details.Embarked = 'Q' OR travel_details.Embarked = 'C'; -- Returns the name and sex of passengers under the age of 27 that embarked at Queenstown and Cherbourg
SELECT count(train_survival.Survived) FROM train_survival JOIN travel_details ON train_survival.PassengerId = travel_details.PassengerId WHERE travel_details.Embarked = 'S';  -- The number of passengers that embarked at Southampton and survived is 645
SELECT passenger_details.PassengerId, Name FROM passenger_details JOIN travel_details ON passenger_details.PassengerId = travel_details.PassengerId WHERE travel_details.Fare > 100 AND passenger_details.Age > 35 AND travel_details.SibSp != 0; -- Returns the id and name of passengers who paid a fare greater than 100 and are above the age of 35 with siblings or spouses on board
SELECT COUNT(passenger_details.PassengerId) FROM passenger_details JOIN travel_details ON passenger_details.PassengerId = travel_details.PassengerId WHERE travel_details.Fare > 100 AND passenger_details.Age > 35 AND travel_details.SibSp != 0; -- The total number of passengers who paid a fare greater than 100 and are above the age of 35 with siblings or spouses on board is 9